import React, { useState } from 'react';
import axios from 'axios';

function WaterForm() {
  const [waterUsage, setWaterUsage] = useState('');
  const [prediction, setPrediction] = useState('');

  const handleSubmit = async (e) => {
    e.preventDefault();
    try {
      const response = await axios.post('http://localhost:5000/predict', { waterUsage });
      setPrediction(response.data.prediction);
    } catch (error) {
      console.error("There was an error with the prediction!", error);
    }
  };

  return (
    <div>
      <form onSubmit={handleSubmit}>
        <label>
          Enter Water Usage (in liters):
          <input
            type="number"
            value={waterUsage}
            onChange={(e) => setWaterUsage(e.target.value)}
            required
          />
        </label>
        <button type="submit">Predict</button>
      </form>
      {prediction && <h2>Predicted Future Usage: {prediction} liters</h2>}
    </div>
  );
}

export default WaterForm;
