import React, { useState } from 'react';
import axios from 'axios';
import './WaterForm.css';

function WaterForm() {
  // State variables
  const [waterUsage, setWaterUsage] = useState('');
  const [prediction, setPrediction] = useState('');
  const [loading, setLoading] = useState(false); // Loading state
  const [error, setError] = useState(''); // Error state

  const handleSubmit = async (e) => {
    e.preventDefault();

    // Clear previous errors and prediction before making the request
    setError('');
    setPrediction('');
    setLoading(true);

    // Ensure water usage is a valid number
    if (!waterUsage || isNaN(waterUsage) || waterUsage <= 0) {
      setError('Please enter a valid positive number.');
      setLoading(false);
      return;
    }

    try {
    const response = await axios.post('http://127.0.0.1:5000/predict', { waterUsage });
    setPrediction(response.data.prediction);
  } catch (error) {
    console.error("There was an error with the prediction!", error);
    setError("There was an error with the prediction. Please try again.");
    } finally {
      setLoading(false);
    }
  };

  return (
    <div className="water-form-container">
      <form onSubmit={handleSubmit}>
        <label>
          Enter Water Usage (in liters):
          <input
            type="number"
            value={waterUsage}
            onChange={(e) => setWaterUsage(e.target.value)}
            required
          />
        </label>
        <button type="submit" disabled={loading}>
          {loading ? 'Predicting...' : 'Predict'}
        </button>
      </form>

      {/* Error Handling */}
      {error && <p className="error">{error}</p>}

      {/* Display Prediction */}
      {prediction && !error && (
        <h2>Predicted Future Usage: {prediction} liters</h2>
      )}
    </div>
  );
}

export default WaterForm;
